class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int cnt = 0;
        bool flag = 1;
        for(int i = n - 1; i >= 0; i--){
            if(s[i] == '0'){
                if(flag){
                    cnt++;
                    flag = 0;
                }
            }else{
                flag = 1;
                ans += cnt;
            }
        }
        return ans;
    }
};
