class Solution:
    def maxOperations(self, s: str) -> int:
        l, r = 0, 0
        n = len(s)
        cnt = 0
        cnt1 = 0
        while l < n:
            while l < n and s[l] == '0':
                l += 1
            while l < n and s[l] == '1':
                cnt1 += 1
                l += 1
            if l < n and s[l] == '0':
                cnt += cnt1
        return cnt
