func maxOperations(s string) int {
    n := len(s)
    var count int
    var res int
    for i := n - 1; i >= 0; i -= 1 {
        if s[i] == '1' {
            if i != n - 1 && s[i + 1] != '1' {
                count += 1
            }
            res += count
        }
    }
    return res
}
