class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        int behind = 0;
        for (int i = 0; i < s.size() - 1; ++i) {
            if (s[i] == '1') {
                int curr = 1;
                int j = i + 1;
                while (j < s.size() && s[j] == '1') {
                    ++curr;
                    ++j;
                }
                if (j == s.size()) {
                    return count;
                } else {
                    i = j - 1;
                    behind += curr;
                    count += behind;
                }
            }
        }
        return count;
    }
};
