class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int n = s.length();
        int f=0;
        int i=0;
        while(i<n-1){
            while(i<n-1 && s[i]=='1'){
                f++;
                i++;
            }
            if(s[i]!='1')cnt+=f;

            while(i<n-1 && s[i]=='0'){
                i++;
            }

        }
        return cnt;
    }
};
