class Solution:
    def maxOperations(self, s: str) -> int:
        op_count = 0
        stack = []
        seen_zero = False
        for c in s:
            if c == "0":
                if not seen_zero:
                    op_count += len(stack)
                    seen_zero = True
            else:
                stack.append(c)
                seen_zero = False

        return op_count
