class Solution {
public:
    int maxOperations(string s) {
        while(not s.empty() and s.back() == '1') s.pop_back();
        int result = 0, prev = s.size(), moves = 0;
        for(int curr = s.size() - 1; curr >= 0; curr--)
            if(s[curr] == '1') {
                if(prev != curr + 1) moves++;
                result += moves;
                prev = curr;
            }
        return result;
    }
};
