class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        for (int i = 0, one = 0; i <= s.size(); i++) {
            if (i < s.size() && s[i] == '0') continue;
            // op
            if (i - 1 >= 0 && s[i - 1] == '0') {
                ans += one;
            }
            // incr
            if (i < s.size() && s[i] == '1') {
                one++;
            }
        }
        return ans;
    }
};
