class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int j = 0;
        char[] sarr = s.toCharArray();
        int n = sarr.length;
        int prevCount = 0;

        while(j < n){
            if(sarr[j] == '1'){
                ans += prevCount;

                while(j < n && sarr[j] == '1'){
                    ++j;
                    ++prevCount;
                }
            }
            else{
                ++j;
            }
        }

        if(sarr[n-1] == '0'){
            ans += prevCount;
        }

        return ans;        
    }
}
