class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int n = s.size();
        int i = n - 1, cnt = 0;
        while (i >= 0) {
            if (s[i] == '1') {
                res += cnt;
                --i;
                continue;
            }
            int j = i;
            while (j >= 0 && s[j] == '0') {
                --j;
            }
            ++cnt;
            i = j;
        }
        return res;
    }
};
