class Solution {
public:
    int maxOperations(string s) {
        int t=0;
        int n=s.size();
        int res=0;
        for(int i=0;i<n;++i){
            if(i+1==n||s[i+1]=='1'){
                if(s[i]=='0'){
                    res+=t;
                }
            }
            if(s[i]=='1'){
                ++t;
            }
        }

        return res;
    }
};
