class Solution {
    public int maxOperations(String s) {
        int res = 0;
        char[] c = s.toCharArray();
        List<Character> list = new ArrayList<>();
        int idx = 0;
        while(idx < c.length){
            if(c[idx] == '1'){
                list.add('1');
                idx++;
            }else{
                while(idx < c.length && c[idx] == '0')
                    idx++;
                list.add('0');
            }
        }
        int j = 0;
        while(j < list.size() && list.get(j) == '0')
            j++;
        int one = j;
        for(int i = j; i < list.size(); i++){
            if(list.get(i) == '0'){
                res += i - one;
                one++;
            }
        }
        return res;
    }
}
