class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int count=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                count++;
            }
            else{
                ans+=count;
                while(s[i]=='0'){
                    i++;
                }
                i--;

            }
        }
        return ans;
    }
};
