class Solution {
public:
    int maxOperations(string s) {
        int cntOfOne = 0;
        int res = 0;
        for (int i = 0; i < s.length(); i++)
        {
            if (s[i] == '1')
            {
                if (i > 0 && s[i-1] == '0')
                {
                    res += cntOfOne;
                }
                cntOfOne++;

            }
            else
            {
                if (i == (s.length()-1))
                {
                    res += cntOfOne;
                }
            }
        }
        return res;
    }
};
