class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        bool flag =false;
        int num1 = 0;
        int n = s.length();
        for (int i=0;i<n;i++)
        {
            if(s[i]=='0')
            {
                flag = true;
            }
            if(s[i]=='1')
            {
                if(flag)
                {
                    ans+=num1;
                }
                num1++;
                flag = false;
            }
        }
        if(flag)
        {
            ans+=num1;
        }
        return ans;
    }
};
