class Solution {
    public int maxOperations(String s) {
        int oneCount = 0, index = 0, operations = 0;
        while (index < s.length()) {
            if (s.charAt(index) == '0') {
                operations += oneCount;
                while (index < s.length() && s.charAt(index) == '0') {
                    index++;
                }
            } else {
                oneCount++;
                index++;
            }
        }
        return operations;
    }
}
