class Solution:
    def maxOperations(self, s: str) -> int:
        N = len(s)

        total = 0
        ones = s.count("1")
        for i in range(N - 1, -1, -1):
            if s[i] == "0" and (i + 1 == N or s[i + 1] == "1"):
                total += ones
            if s[i] == "1":
                ones -= 1
        return total
