class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int now = 0;
        int add = 0;
        for(int i = s.size() - 1; i >= 0; i--){
            if(s[i] == '0'){
                add = 1;
            } else {
                now += add;
                add = 0;
                ans += now;
            }
        }
        return ans;
    }
};
