class Solution {
    public int maxOperations(String s) {
        char[] ch = s.toCharArray();
        int ans = 0;
        int len = 0, sum = 0;
        for (char c : ch) {
            if (c == '1') {
                ++len;
            } else if (len > 0){
                sum += len;
                ans += sum;
                len = 0;
            }
        }
        return ans;
    }
}
