class Solution:
    def maxOperations(self, s: str) -> int:
        start1 = 0
        end1 = -1
        pos0 = -1
        n = len(s)
        ans = 0
        leiji = 0
        while start1 < n and s[start1] == '0':
            start1 += 1
        if start1 >= n - 1:
            return 0
        # print(start1)
        while start1 < n:
            end1 = start1
            while end1 + 1 < n and s[end1 + 1] == '1':
                end1 += 1
            # 此时end1是连续的1的最后一个位置
            if end1 < n - 1:
                ans += end1 - start1 + 1 + leiji
                leiji += end1 - start1 + 1
            # print(ans, leiji)
            start1 = end1 + 2
            # 找下一个1
            while start1 < n and s[start1] == '0':
                start1 += 1
            if start1 >= n - 1:
                return ans
        return ans
