class Solution {
public:
    int maxOperations(string s) {
        vector<int> a;
        int now = 0, tot = 0;
        for (auto i : s) {
            if (i == '0') {
                if (now) a.push_back(now);
                now = 0;
            }
            else {
                now++;
                tot++;
            }
        }
        if (now) a.push_back(now);
       // for (auto i : a) cout << i << ' ';
        //cout << endl;
        int n = a.size(), hv = 0, ans = 0;
        for (int i = 0; i < n - 1; i++) {
            hv += a[i];
            ans += hv;
        }
        if (!now) ans += tot;
        return ans;
    }
};
