class Solution:
    def maxOperations(self, s: str) -> int:
        # find all indexes of 1's.
        # for any gap larger than 1 between them, add a move for all before it
        indexes = []
        for i, char in enumerate(s):
            if char == "1":
                indexes.append(i)
        ans = len(indexes) if indexes and indexes[-1] != len(s) - 1 else 0
        for i in range(len(indexes) - 2, -1, -1):
            if indexes[i] + 1 != indexes[i + 1]:
                ans += (i + 1)
        return ans
