class Solution:
    def maxOperations(self, s: str) -> int:
        # Total operations counter
        total_operations = 0
        # Counter for number of '1's encountered so far
        ones_count = 0
        zero = False
        # Traverse the string
        for char in s:
            if char == '1':
                if zero:
                    total_operations += ones_count
                    zero = False
                ones_count += 1
            else:
                zero = True
        if zero:
            total_operations += ones_count
        return total_operations
