package main

import "fmt"

func maxOperations(s string) int {

	n := len(s)
	f := false
	t := 0
	b := 0
	for i := n - 1; i >= 0; i-- {
		if s[i] == '1' && !f {
			continue
		}
		if s[i] == '0' && (i == n-1 || s[i+1] != '0') {
			f = true
			b++
		}
		if f && s[i] == '1' {
			t += b
		}
	}
	return t

}
