class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), cnt = 0, ans = 0, ind = 0;
        while(ind < n) {
            while(ind < n && s[ind] == '1') {
                cnt++;
                ind++;
            }
            if(ind < n && s[ind] == '0') {
                ans += cnt;
                while(ind < n && s[ind] == '0') {
                    ind++;
                }
            }
        }
        return ans;
    }
};
