class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cntOne = 0, res = 0;
        for(int i = 0, j; i < n; i ++)
        {
            if(s[i] == '0') continue;
            j = i;
            while(j < n - 1 && s[j + 1] == '1') j ++;

            cntOne += j - i + 1;

            if(j != n - 1 && s[j + 1] == '0') res += cntOne;
            i = j;
        }

        return res;
    }
};
