const int N = 30010;
const int mod = 1000000007; // 10^9+7

class Solution {
public:
	int maxOperations(string s) {
		int n = sz(s);
		int cnt = 0;
		forn (i, n-1) {
			if (s[i] == '1' && s[i+1] == '0') cnt++;
		}
		vi pos;
		forn (i, n) if (s[i] == '1') pos.push_back(i);
		int ans = 0;
		int m = sz(pos);
		forn (i, m) {
			if (i != 0) {
				if (pos[i] - pos[i-1] > 1) cnt--;
			}
			ans += cnt;
		}

		return ans;
	}
};
