class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int prevOne = n;
        int prevMoveCount = 0;
        int i = n - 1;
        int count = 0;
        while(i >= 0) {
            if(s[i] == '1') {
                if(prevOne - i == 1) {
                    count += prevMoveCount;
                } else {
                    count += (prevMoveCount + 1);
                    prevMoveCount++;
                }
                prevOne = i;
            }
            i--;
        }
        return count;
    }
};
