class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int ans = 0;
        int c = 0;
        for(int i=0;i<n;i++){
            if(s.charAt(i) == '1') c++;
            else{
                while(i < n && s.charAt(i) == '0') i++;
                ans += c;
                i--;
            } 
        }
        return ans;
    }
}
