    int maxOperations(string s) {
        int ones_till_now=0;
        int ans=0;
        char prev='0';
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                ones_till_now++;
            }
            else if(s[i]=='0'&&prev=='0'){
                int temp=0;
            }
            else{
                ans+=ones_till_now;
            }
            prev=s[i];
        }
        return ans;
    }
};
