class Solution {
public:
    int maxOperations(const string& s) {

        int ans = 0;
        for(int i=0, ones=0, prev=0; i<s.size(); ++i) {

            if (s[i]=='1') {
                ++ones;
                prev=1;
            } else if (s[i]=='0' && prev==1) {
                ans += ones;
                prev = 0;
            }


        }

        return ans;
    }
};
