class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long A = 0, B = 0, pos = 0, neg = 0;
        nums.insert(nums.begin(), 0);
        target.insert(target.begin(), 0);
        for (int i = 0; i + 1 < (int)nums.size(); ++i) {
            A = nums[i + 1] - nums[i];
            B = target[i + 1] - target[i];
            if (A > B) {
                pos += A - B;
            } else {
                neg += B - A;
            }
        }

        return max(pos, neg);
    }
};
