class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> a(n);
        for(int i=0; i<n; ++i)  a[i] = target[i] - nums[i];
        for(int i=n-1; i>0; --i)    a[i] -= a[i-1];
        long long s = 0, t = 0;
        for(int i=0; i<n; ++i){
            if(a[i] >= 0)   s += a[i];
            else  t += a[i];
        }
        return max(s, abs(t));
    }
};
