using namespace std;
using ll = long long;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        long long ans = 0;
        a.push_back(0);
        b.push_back(0);
        int n = a.size();
        ll add = 0, sub = 0;
        for(int i = 0; i < n; i++) {
            int d1 = a[i];
            int d2 = b[i];
            if(i) {
                d1 -= a[i - 1];
                d2 -= b[i - 1];
            }

            if(d1 < d2) {
                add += (d2 - d1);
            } else {
                sub += (d1 - d2);
            }

        }
        return max(add, sub);
    }
};
