class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int result = 0;

        int prevInCrementCount = 0;

        int n = target.size();
        for(int i = 0; i < n; i++) {
            long long int diff = target[i] - nums[i];
            if(diff > 0) {
                if(prevInCrementCount < 0) {
                    result += diff;
                } else {
                    if(prevInCrementCount < diff) {
                        result += (diff - prevInCrementCount);
                    }
                }
            } else {
                if(prevInCrementCount > 0) {
                    result += (-diff);
                } else {
                    if(prevInCrementCount > diff) {
                        result += (-diff + prevInCrementCount);
                    } 
                }
            }
            prevInCrementCount = diff;
            //cout << i << " result = " << result << " prevInCrementCount = " << prevInCrementCount << endl;
        }
        return result;
    }
};
