class Solution {

    int sign(int x) {
        if (x > 0) return 1;
        else if (x < 0) return -1;
        return 0;
    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        vector<int> diff;
        for (int i = 0; i < n; i++) {
            diff.push_back(target[i] - nums[i]);
        }
        long long ans = 0;
        int add = 0;
        for (int i = 0; i < n; i++) {
            if (sign(diff[i]) != sign(add)) {
                ans += abs(diff[i]);
                add = diff[i];
            } else if (sign(diff[i]) > 0) {
                if (diff[i] > add) {
                    ans += diff[i] - add;
                }
                add = diff[i];
            } else if (sign(diff[i] < 0)) {
                if (diff[i] < add) {
                    ans += add - diff[i];
                }
                add = diff[i];
            }
        }
        return ans;
    }
};
