class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] diffPos = new int[nums.length];
        int[] diffNeg = new int[nums.length];

        for(int i=0;i<target.length;i++){
            int diff = target[i]-nums[i];
            if(diff>0){
                diffPos[i] = diff;
            } else {
                diffNeg[i] = Math.abs(diff);
            }
        }
        long count = Math.abs(target[0]-nums[0]);
        for(int i=1;i<target.length;i++){
            if(diffPos[i]-diffPos[i-1]>0){
                count+=diffPos[i]-diffPos[i-1];
            }
            if(diffNeg[i]-diffNeg[i-1]>0){
                count+=diffNeg[i]-diffNeg[i-1];
            }
        }
        return count;
    }
}
