class Solution {
public long minimumOperations(int[] nums, int[] target) {
        int last = 0;
        long ans = 0;
        for (int i = 0; i < nums.length; i++) {
            int cur = nums[i] - target[i];
            if (cur > 0) {
                if (last > 0) {
                    ans += last < cur ? cur - last : 0;
                } else {
                    ans += cur;
                }
            } else if (last < 0) {
                ans += last > cur ? last - cur : 0; 
            } else {
                ans -= cur;
            }
            last = cur;
        }
        return ans;
    }
}
