class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        dif = [0] + [target[i] - nums[i] for i in range(n)]
        print(dif)
        res = 0
        for i in range(1, n + 1):
            if (dif[i] > 0) ^ (dif[i - 1] > 0):
                res += abs(dif[i])
            else:
                res += max(abs(dif[i]) - abs(dif[i - 1]), 0)
            #print(f"i={i} res={res}")
        return res
