class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        bool pos = true;
        long long prev_diff=0;
        long long ans=0;
        for(int i=0; i<n; i++){
            long long diff = 1ll*target[i] - 1ll*nums[i];
            if(prev_diff*diff>=0){
                if(abs(diff)> abs(prev_diff)) {
                    ans += abs(diff - prev_diff);
                }
            }
            else{
                ans += abs(diff);
            }
            prev_diff = diff;    
        }
        return ans;
    }
};
