class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n=nums.length;
        int[] minus = new int[n];
        for(int i=0;i<n;++i) minus[i]=target[i]-nums[i];

        long[] dp = new long[n];
        dp[0]=Math.abs(minus[0]);
        // long ans=dp[0];
        for(int i=1;i<n;++i){
            if(minus[i]>=0 && minus[i-1]>=0){
                if(minus[i]<=minus[i-1]) dp[i]=dp[i-1];
                else dp[i]=dp[i-1]+minus[i]-minus[i-1];
            } else if(minus[i]<=0 && minus[i-1]<=0){
                if(minus[i]>=minus[i-1]) dp[i]=dp[i-1];
                else dp[i]=dp[i-1]+minus[i-1]-minus[i];
            } else {
                dp[i]=dp[i-1]+Math.abs(minus[i]);
            }
            // ans+=dp[i];
        }
        return dp[n-1];
    }
}
