class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> b(n);
        for (int i = 0; i < n; ++i) {
            b[i] = target[i] - nums[i];
        }
        long long ans = 0, cut = 0;
        int st = 0;
        int pos = -1;
        for (int i = 0; i < n; ++i) {
            if (b[i] == 0) {
                pos = -1;
                ans += st - cut;
                cut = st = 0;
                continue;
            }
            if (pos == -1) {
                pos = b[i] > 0 ? 1 : 0;
                st = abs(b[i]);
            } else if (pos == 1 && b[i] > 0 || pos == 0 && b[i] < 0) {
                if (abs(b[i]) >= st) {
                    st = abs(b[i]);
                } else {
                    ans += st - cut;
                    st = cut = abs(b[i]);
                }
            } else {
                ans += st - cut;
                pos ^= 1;
                st = abs(b[i]);
                cut = 0;
            }
        }
        return ans + st - cut;
    }
};
