class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> a(n);
        for(int i = 0; i < n; i++) a[i] = target[i] - nums[i];
        long long ans = 0;
        long long f = 1, high = 0;
        for(int i = 0; i < n; i++){
            if(a[i] == 0) high = 0;
            else if(f == a[i] / abs(a[i])){
                if(f > 0){
                    ans += max(0LL, a[i] - high);
                    high = a[i];
                }
                else{
                    ans += max(0LL, high - a[i]);
                    high = a[i];
                }
            }
            else{
                ans += abs(a[i]);
                high = a[i];
                f = a[i] / abs(a[i]);
            }
        }
        return ans;
    }
};
