class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> a(n);
        for (int i = 0; i < n; i++) {
            a[i] = nums[i] - target[i];
        }
        for (int i = 0; i < n; ) {
            int idx = i;
            bool neg = (a[i] < 0);
            long long maxi = 0, prev = 0;
            while (idx < n && ((neg && a[idx] < 0) || (!neg && a[idx] >= 0))) {
                maxi = max(maxi, 1ll * abs(a[idx]) - prev);
                if (abs(a[idx]) - prev < maxi) {
                    ans += maxi;
                    prev = 1ll * abs(a[idx]);
                    maxi = 0;
                }
                idx++;
            }
            ans += maxi;
            i = idx;
        }
        return ans;
    }
};
