class Solution {
public:
    int minNumberOperations(vector<int>& target) 
    {
        int ans = target[0];
        for(int i = 1; i < target.size(); i++)
        {
            ans += max(0,target[i]-target[i-1]);
        }
        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>diff;
        int n=nums.size();
        for(int i=0;i<n;i++)
        {
            diff.push_back(target[i]-nums[i]);
        }


        // for(auto &i:diff)
        // {
        //     cout<<i<<" ";
        // }
        // cout<<endl;

        long long ans=0;
        for(int i=0;i<n;)
        {
            if(diff[i]>0)
            {
                vector<int>temp;
                while(i<n&&diff[i]>0)
                {
                   temp.push_back(diff[i]);
                    i++;
                }
                ans+=minNumberOperations(temp);
            }
            else if(diff[i]==0)
            {
                i++;

            }
            else
            {
                vector<int>temp;
                while(i<n&&diff[i]<0)
                {
                    temp.push_back(diff[i]*(-1));
                    i++;
                }
                ans+=minNumberOperations(temp);
            }

        }


        return ans;

    }
};
