class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        arr = [tar - num for tar, num in zip(target, nums)]
        res = 0
        pos = arr[0] > 0
        pre = 0
        for diff in arr:
            if diff == 0 or (diff > 0) != pos:
                pre = 0
                pos = diff > 0
            diff = abs(diff)
            if diff > pre:
                res += diff - pre
            pre = diff
        return res
