class Solution {
public:
    long long minimumOperations(vector<int>& n, vector<int>& t) {
        long long ans=0;
        long long ans2=0;
        long long val=0;
        int i=0;
      while(i<n.size())
      {
        ans=0;
        ans2=0;
        while(i<n.size() && n[i]>=t[i])
        {
            if(n[i]-t[i]>=ans)
            {
                ans=n[i]-t[i];
            }
            else
            {
                val+=ans-(n[i]-t[i]);
                ans=n[i]-t[i];
            }
             i++;
        }
        while(i<n.size() && n[i]<t[i])
        {
            if(t[i]-n[i]>=ans2)
            {
                ans2=t[i]-n[i];
            }
            else{
                val+=ans2-(t[i]-n[i]);
                ans2=t[i]-n[i];
            }
            i++;
        }

        val+=ans+ans2;
      } 
      return val; 
    }
};
