class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # delta = deque([0])

        # for d in (a - b for a, b in zip(target, nums)):
        #     if d == delta[-1]:
        #         continue
        #     delta.append(d)

        delta = deque([a - b for a, b in zip(target, nums)])

        ret = 0
        prev = 0

        for d in delta:
            if d > 0:
                if prev > 0:
                    ret += d - min(prev, d)
                else:
                    ret += d
            elif d < 0:
                if prev < 0:
                    if prev < d:
                        ret += 0
                    else:
                        ret += abs(d - prev)
                else:
                    ret += abs(d)                    
            prev = d

        return ret
