impl Solution {
    pub fn minimum_operations(nums: Vec<i32>, target: Vec<i32>) -> i64 {
        let mut arr = vec![];
        for i in 0..nums.len() {
            arr.push( nums[i] - target[i]);
        }
        println!("{:?}", arr);

        let mut ans : i64 = 0;
        let mut pre = 0;
        for val in arr {
            let val = val as i64;
            if (val * pre) <= 0 {
                ans += val.abs();
            } else {
                let val = val.abs();
                let pre = pre.abs();
                if pre < val {
                    ans += val - pre;
                }
            }
            pre = val;
        }
        ans
    }
}
