class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ar = [nums[i] - target[i] for i in range(n)]

        def getm(arr, dd=0):
            m = len(arr)
            F = arr[:]
            S = []
            for i, x in enumerate(arr):
                while S and S[-1] > x - dd:
                    S.pop()
                if S:
                    F[i] = min(F[i], x - S[-1])
                S.append(x)
            return F

        def solve(arr):
            arr = [abs(i) for i in arr]
            #print(arr)
            m = len(arr)
            F1 = getm(arr)
            F2 = getm(arr[::-1], 1)
            ans = 0
            for i in range(m):
                ans += min(F1[i], F2[m - 1 - i])
            #print(ans)
            return ans

        ans = 0
        cur = []
        for x in ar:
            if x == 0:
                ans += solve(cur)
                cur = []
            elif cur and cur[-1] * x < 0:
                ans += solve(cur)
                cur = [x]
            else:
                cur.append(x)
        ans += solve(cur)
        return ans
